extension karnview: UITableViewDataSource, UITableViewDelegate{
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return karn.count

    }
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let type = tableView.dequeueReusableCell(withIdentifier: "type1", for: indexPath) as! typeTableViewCell
        type.lbl.text = karn[indexPath.row]
        type.img.image = UIImage(named: karn[indexPath.row])
        return type
    }
    func tableView(_ tableView: UITableView, heightForRowAt indexPath: IndexPath) -> CGFloat {
        return 120
    }

    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        let move = storyboard?.instantiateViewController(withIdentifier: "animal") as!
        deskripsion
        
        move.imgd = UIImage(named: karn[indexPath.row])!
        move.txtd = desckarn[indexPath.row]
        move.lbld = karn[indexPath.row]
        
        self.navigationController?.pushViewController(move, animated: true)
    }
